	return tmp;
}

function(threadURL) {
	var ofs = "offset="
	var eofs = "&" + ofs;
	var qofs = "?" + ofs;
	
	var n = (threadsucker_Globals.linkIndex-1)*24;
	var tmp = threadURL.replace(/offset=\d+/, ofs + n);
	
	return (tmp.match(/\/\?/)) ? threadURL + eofs + n : threadURL + qofs + n;
}

