function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/var url = '(.+?)'/);
	if (!iUrl) iUrl = pageData.match(/<div id="continue"(?:.|\s)+?href="\/\d+\/(.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1].replace(/^(http:\/)\/?/, "$1/");
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

