function(pageData, pageUrl) {
   var theId = "mainImage";

   var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
   var tmpMatch = pageData.match(searchPat);
   if (tmpMatch) {
		var the_url = tmpMatch[0].match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
		}

   var retVal = {};

   if(!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
   else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
		}

   try {
		var album = pageData.match(/<title>bayimg - image: (.*?) - free uncensored image hosting<\/title>/)[1];
		var image = pageData.match(/Image: (\d+) of (\d+)/);
		if (image) {album = album + "-" + image[1] + "_" + image[2]};
		var fileName = the_url.substring(the_url.lastIndexOf("/")+1);
		retVal.fileName = album + "-" + fileName;
		}
   catch(e) {
		retVal.fileName = Math.random().toString().substring(2) + ".jpg";
		}

   return retVal;
   }

