function(pageData, pageUrl) {
	var retVal = {};
	var tooty = pageUrl.match(/(http:\/\/.+?)\//);
	var fruity = pageData.match(/<a href="(.+?)" id="photo_gne_button_zoom"/);
	var iUrl = null;

	if (fruity) {
		var newUrl = tooty[1] + fruity[1];
		var req = new XMLHttpRequest();
		req.open("GET", newUrl, false);
		req.send(null);

		var newPage = req.responseText;
		var fartSniffer = newPage.match(/<img.+src=("|')(http:\/\/.+\.static\.flickr\.com\/\d+\/\d+_.+_.\..{3,4})\1/);
		if (fartSniffer) iUrl = fartSniffer[2];
		}
	else {
		var imageInfo = pageData.match(/'0': .+/);
		if (imageInfo) {
			imageInfo = imageInfo[0];

			var original = imageInfo.match(/"label":"Original".+?"url":"(.+?)"/);
			var large = imageInfo.match(/"label":"Large".+?"url":"(.+?)"/);
			var med640 = imageInfo.match(/"label":"Medium 640".+?"url":"(.+?)"/);
			var medium = imageInfo.match(/"label":"Medium".+?"url":"(.+?)"/);
			var blah = pageData.match(/<img.+src=('|")(http:\/\/.+\.static\.flickr\.com\/\d+\/\d+_.+?\..{3,4})\1/);	

			if (original) iUrl = original[1];
			else if (large) iUrl = large[1];
			else if (med640) iUrl = med640[1];
			else if (medium) iUrl = medium[1];
			else if (blah) iUrl = blah[2];
			}
		}

	if (!iUrl) {
		var JSONImageInfo = pageData.match(/"sizes":{(?:.+?)}}/);
		if (JSONImageInfo) iUrl = JSONImageInfo[0].match(/"url":"([^"]+)"[^}]+}}/); 
		if (iUrl) iUrl = iUrl[1];
		}
	if (!iUrl) {
		var imageInfo = pageData.match(/sizes: {(?:.|\s)+?},\s+tags:/);
		if (imageInfo) iUrl = imageInfo[0].match(/(?:.|\s)+url: '([^']+)'/); 
		if (iUrl) iUrl = iUrl[1];
		}

	if (iUrl) {
		retVal.imgUrl = iUrl.replace(/\\\//g, "/");
		retVal.status = "OK";
		}
	else {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}

	return retVal;
	}

