function(pageData, pageUrl) {
	var retVal = {};
	var sPattern = pageData.match(/window.location="(.+)"/);	

	if (!sPattern) {
		var sPattern2 = pageData.match(/http:\/\/img\d+\.hotlinkimage\.com\/showimg\.php\?id=\d+&v=\d+&ext=jpg&dt=1/);

		if(!sPattern2) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = sPattern2;
			retVal.status = "OK";

			try {
				retVal.fileName = sPattern2[0].match(/id=(\d+)/)[1] + ".jpg";
				}
			catch(e) {
				retVal.fileName = Math.random().toString().substring(2) + ".jpg";
				}
			}	
		}
	else {
		retVal.imgUrl = sPattern[1];
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

