function(pageData, pageUrl) {
    var retVal = {};

	/* Patterns provided by Flo:
		http://([0-9]+).imagebam.com/download.php?ID=([0-9]+)&salt=([a-zA-Z0-9]{6})&sec=([a-zA-Z0-9]{6})&filesize=([0-9]+)&filename=(.*)
		http://([0-9]+).imagebam.com/download/([a-zA-Z0-9_-])/([0-9]+)/([0-9]+)/(.*)
	*/

    var sPattern = pageData.match(/("|')(http:\/\/\d+\.imagebam\.com\/(?:download|dl)\.php\?ID=\d+&amp;salt=\w+&amp;sec=\w+&amp;filesize=\d+&amp;filename=.+?)\1/);
	if (!sPattern) sPattern = pageData.match(/("|')(http:\/\/\d+\.imagebam\.com\/download\/[^\/]+\/\d+\/\d+\/.+?)\1/);
	if (!sPattern) sPattern = pageData.match(/href=("|')(http:\/\/\w+\.amazonaws\.com\/bambackup\/.+?)\1/);

    if (!sPattern) {
        retVal.imgUrl = null;
        retVal.status = "ABORT";
        }
    else {
        retVal.imgUrl = sPattern[2].replace(/&amp;/g, "&");
        retVal.status = "OK";
        var fname = sPattern[2].match(/filename=(.+\..{3,4})/);
		if (fname) retVal.fileName = fname[1];
		else {
			fname = sPattern[2].match(/\/bambackup\/([^\?]+)/);
			if (fname) retVal.fileName = fname[1] + ".jpg";
			}
		}

    return retVal;
    }

