	if (pageData.match(/continuetoimage/)) {
		var req = new XMLHttpRequest();
		req.open("POST", "http://imgcandy.net/lofasz.php", false);
		req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
		var postData = "imgContinue=Continue+to+image+...+"; 
		req.send(postData);

		var iUrl = pageData.match(/<div id='continuetoimage'>\s*<form action='(.+?)'/);
		if (iUrl) {
			retVal.imgUrl = iUrl[1];
			retVal.status = "REQUEUE";
		}
	}
	else {
		var iUrl = pageData.match(/href=('|")(http:\/\/[^/]+\/upload\/big\/.+?)\1/);
		if (!iUrl) iUrl = pageData.match(/src=('|")(http:\/\/[^/]+\/upload\/big\/.+?)\1/);
		if (iUrl) {
			retVal.imgUrl = iUrl[2];
			retVal.status = "OK";
		}
	}

	return retVal;
}

