	if (iUrl) iUrl = iUrl[1].replace(/(https?:\/\/\w+\.ggpht\.com\/.+\/.+\/.+\/.+\/)(.+\..{3,4})$/, "$1d/$2");

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl;
		retVal.status = "OK";
		}

	return retVal;
	}

