function(pageData, pageUrl) {
	var retVal = {};

	// use the following 4 lines if you are using the id attribute to find the image source.
	// just modify the "theId" variable to reflect the proper ID
	var theId = "img";

	var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1(?:.|\\s)+?>");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var imgMatch = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/i);
		}
	if(imgMatch) {
		retVal.imgUrl = imgMatch[2];
		retVal.status = "OK";
		retVal.fileName = Math.random().toString().substring(2) + ".jpg";
		return retVal;
		}

	var redirMatch = pageData.match(/<a href="(http:\/\/img\d+\.picfoco\.com\/img\.php\?id=.*&q=.*&jump=.*&ru=.*?)">/);
	if(redirMatch) {
		retVal.imgUrl = redirMatch[1];
		retVal.status = "REQUEUE";
		retVal.referer = pageUrl;
		}

	else {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}

	return retVal;
	}

