function(pageData, pageUrl) {
	// chickupload keeps track of the number of connections you are making to it.  If
	// it senses that you are making an unreasonable amount of simultaneous connections,
	// it will block your connections from the actual image.
	//
	// For "ImageHost Grabber", the implementation is to change the number of simultaneous
	// downloads to 1 by modifying the global variable "maxThreads"
	if (ihg_Globals.appName == "ImageHost Grabber") ihg_Globals.maxThreads = 1;

	var theId = "full";

	var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var the_url = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
		}


	// retVal.imgUrl is the string representation to the url of the target image
	// retVal.status is a string representation of the status.  Acceptable values
	// for status are: OK, ABORT, RETRY, REQUEUE
	//
	// You may add other members to retVal for use with your program if need be.
	// However, imgUrl and status should always be present for the sake of compatibility.
	var retVal = new Object();

	if(!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
		}

	// If it can find the original file name, it will use that instead.
	// otherwise, it picks a random file name
	//try {
	//	retVal.fileName = pageData.match(/&raquo; <a href="\/gallery\/.+?\/.+?\/.+?">\w*<\/a> &raquo; (.+\.\w+)\s\(views: \w*\)/)[1];
	//	}
	//catch(e) {
	//	retVal.fileName = Math.random().toString().substring(2) + ".jpg";
	//	}

	return retVal;
	}

