function(pageData, pageUrl) {
	var retVal = new Object();

	var theId = "highres";

	var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var the_url = tmpMatch.match(/href(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
		}
	else {
		theId = "image";
		searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
		tmpMatch = pageData.match(searchPat);
