function(pageData, pageUrl) {
	var retVal = new Object();

	var site = pageUrl.match(/^http:\/\/img\d+\.imgiga\.com\//);
	var iUrl = pageData.match(/<script.+?src="(imgiga\/showimg\.php.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		var newUrl = site[0] + iUrl[1];
		var req = new XMLHttpRequest();
		req.open("GET", newUrl, false);
		req.setRequestHeader("Referer", pageUrl);
		req.send(null);

		var newPage = req.responseText;
		iUrl = newPage.match(/href="(http:\/\/.+?\/image\/.+?)"/);

		if (!iUrl) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = iUrl[1];
			retVal.status = "OK";
			}
		}

	return retVal;
	}

