			Z = A + 26;
		while (i--) {
			c = __file_url.charCodeAt(i);
			if (c >= a && c < z) {
				b[i] = String.fromCharCode(((c - a + 13) % (13 * 2)) + a);
			} else if (c >= A && c < Z) {
				b[i] = String.fromCharCode(((c - A + 13) % (13 * 2)) + A);
			} else {
				b[i] = __file_url.charAt(i);
			}
		}
		__file_url = b.join('');

		var main_url = pageData.match(/s1.addParam\('flashvars','file=(.+?)'/);
		main_url = main_url[1];
		iUrl = main_url + __file_url + ".flv";*/

		iUrl = pageData.match(/file=(.+?)&/)[1];
		}
	else {
		iUrl = pageData.match(/<img\s+src="(http:\/\/s\d+\.motherless(?:media)?\.com\/dev.+\/\d+\/\d+\/\d+\/.+\..{3,4})"\s+style="/);
		if (iUrl) iUrl = iUrl[1];
		}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl;
		retVal.status = "OK";
		}

	return retVal;
    }

