		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
   var retVal = new Object();

   retVal.imgUrl = pageUrl;
   retVal.status = "OK";

   return retVal;
   }

function(pageData, pageUrl) {
	var retVal = new Object();

	var iUrl = pageData.match(/Size of this preview.+?href="(.+?)"/);

	if (!iUrl) iUrl = pageData.match(/No higher resolution available.+?href="(.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

