	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = new Object();

	var iUrl = pageData.match(/href="(.+\/enlargedimage\/.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

