function(pageData, pageUrl) {
	var retVal = new Object();

	var iUrl = pageData.match(/<img src="(http:\/\/[^\/]+\/foto\/.+?\/g\d+\/\d+\/[^\/]+\..{3,4})"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1].replace(/\/\d+(\/[^\/]+\..{3,4})$/, "$1");
		retVal.status = "OK";
		}

	return retVal;
	}

