function(pageData, pageUrl) {
	var retVal = new Object();

	var medium = pageUrl.match(/^http:\/\/(?:www\.)?z0d\.com\/\?page=gallery&image_size=medium/);
	if (medium) {
		retVal.imgUrl = pageUrl.replace(/(image_size=)medium/, "$1large");
		retVal.status = "REQUEUE";
		return retVal;
		}

	var iUrl = pageData.match(/src="(\/content\/\d+\/\d+\/\d+\/.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

